function initializePagination(a){if(a==undefined){a=""}$(a+" .paginate").each(function(b,d){var c=$(d);c.dataTable({bJQueryUI:true,bFilter:false,bLengthChange:false,iCookieDuration:86400,sPaginationType:"full_numbers",iDisplayLength:10,aoColumnDefs:[{bSortable:false,aTargets:["nonsortable"]}]})});$(a+" .dataTables_wrapper").each(function(b,d){var c=$(d);if(c.find("span span.ui-state-default:not(.ui-state-disabled)").size()==0){c.find("div.fg-toolbar").css("display","none")}else{c.find("div.fg-toolbar").css("display","block");c.find("div.ui-toolbar").first().hide()}})}$(function(){initializePagination()});